"""# Reading the stars from the sky"""

from matplotlib import pyplot as plt #import matplotlib.pyplot as plt
from skimage import data
from skimage.feature import blob_dog, blob_log, blob_doh
from skimage.color import rgb2gray
from skimage.io import imread
import math

img8 = imread('wint_sky.gif','gray')
plt.imshow(img8, "gray")
plt.show()

print(type(img8))
print(img8.shape)
print(img8.ndim)
print(img8.size)
print(img8.itemsize)
print(img8[150:180,10:40])

#Blobs are bright on dark or dark on bright regions in an image. 
blobs_log = blob_log(img8, max_sigma=30, num_sigma=10, threshold=.1)
numrows = len(blobs_log)
print("Numer of stars counted : " ,numrows)

fig, ax = plt.subplots(1, 1)
plt.imshow(img8, 'gray')
for blob in blobs_log:
    y, x, r = blob
    c = plt.Circle((x, y), r+5, color='lime', linewidth=2, fill=False)
    ax.add_patch(c)

